<?phpclass Registration extends CI_Model{    public function registration()    {        $Name = $this->input->post('Name');        $LastName = $this->input->post('LastName');        $Role = $this->input->post('Role');        $Email = $this->input->post('Email');        $Password = md5($this->input->post('Password'));        $data = array(            'Name' => $Name,            'LastName' => $LastName,            'Email' => $Email,            'Password' => $Password,            'Role' => $Role        );        if (isset($_POST['registration'])) {            $this->load->helper(array('form', 'url'));            $this->load->library('form_validation');            $this->form_validation->set_rules('Name', 'Name', 'required');            $this->form_validation->set_rules('Password', 'Password', 'required',                array('required' => 'You must provide a %s.')            );            $this->form_validation->set_rules('PasswordConfirm', 'Password Confirmation', 'required');            $this->form_validation->set_rules('Email', 'Email', 'required');            if ($this->form_validation->run() == false) {                $this->load->view('includes/full_registration');            } else {                //insert into users                $this->db->set($data);                $this->db->insert('users');                //load success page                $this->load->view('includes/success_reg');            }        }    }}